import Vue from 'vue'
import axios from 'axios'
import App from './App.vue'
import router from './router'
import ElementUI from 'element-ui'
import 'element-ui/lib/theme-chalk/index.css'
import '@/assets/css/global.css'
import '@/assets/css/theme/index.css'
import request from "@/utils/request";

Vue.prototype.$eventBus = new Vue();//全局时间总线

axios.defaults.baseURL = 'http://localhost:9090';  // 设置基础 URL（根据你的实际情况修改）

Vue.prototype.$eventBus.$on('addressUpdated', (newAddress) => {
    const user = JSON.parse(localStorage.getItem('xm-user') || '{}');
    user.address = newAddress;
    localStorage.setItem('xm-user', JSON.stringify(user));
  });

Vue.prototype.$axios = axios;  // 挂载 axios 到 Vue 实例

Vue.config.productionTip = false

Vue.prototype.$request = request
Vue.prototype.$baseUrl = process.env.VUE_APP_BASEURL

Vue.use(ElementUI, {size: "small"})

new Vue({
    router,
    render: h => h(App)
}).$mount('#app')
